    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String usuario = txtUsuario.getText();
        String contrasenia = txtpasword.getText();
        String leyenda1 = "BIENVENIDO USUARIO";
        String leyenda2 = "LOS DATOS INGRESADOS SON INCORRECTOS";
        
        if (usuario.equalsIgnoreCase("admin") && contrasenia.equalsIgnoreCase("123Prueba")) {
            textArea.setText(leyenda1);
        } else {
            textArea.setText(leyenda2);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

